import React from 'react'
export default class PixyImgBox extends React.Component {
    constructor(props, context) {
      super(props, context);
  
      this.state = {
        checked: false
      };
      this.count = 0;
      this.data = [];
      this.boxes = [];
      this.width = 0;
      this.height = 0;
      this.left = 0;
      this.top = 0;
      this.seq = -4;
      this.whiteBox = [];
    }
  
    //This sends a json string with just a cmd specified to the robot
    sendCmd() {
      const ip = "http://localhost:3000/getPixyData";
      return  fetch(ip).then((resp) => resp.json()).then(function(data) {
        return data;
      })
  
    }
  
    async pixyBoxInfo() {
      //const getPixyInfoCmd = 5;
