      //   id: i,
      //   key: i,
      //   width: box[0] * 100 + '%',
      //   height: box[1] * 100 + '%',
      //   left: box[2] * 100 + '%',
      //   top: box[3] * 100 + '%',
      // }})}))))

      return (
        <div class="pixyView">
          <header id="lunaHeader">•~•~•~•~•~•~•~•~•~ Pixy View ~•~•~•~•~•~•~•~•~•</header>
          <div id="pixyViewBox">{this.boxes.map((box, i) => {return (
            <div style={{color: 'black',
              position: 'absolute',
              border: '1px solid black',
              backgroundColor: '#f4f4f4',
              zIndex: '99999999999999',
              width: "box[0] * 100 + '%'",
              height: "box[1] * 100 + '%'",
              left: "box[2] * 100 + '%'",
              top: "box[3] * 100 + '%'",}}>
            </div>
          )})}
          </div>
          <style jsx>{`
                html, body {
                  margin: 0;
                  width: 100%;
                  height: 100%;
                  overflow-y: hidden;
                }
                #pixyView {
                  width:100%; 
                  float:center; 
                  height: 25px;
                  margin: 0px;
                  padding-top: 0.5%;
                  position: relative;
                }
                #lunaHeader{
                  color: rgb(206, 206, 206);
                  width: 100%;
                  height: 35px;
                  background-color: #333333;
                  font-size: 25px;
                  text-align: center;
                  border-bottom: solid black 1px;
                  box-shadow: 0px 0px 5px #888888;
                }
                #pixyViewBox {
                  width:100%; 
                  height: 325px; 
                  /* float:center;  */
                  background: rgb(71, 68, 68);
                  margin: 0px;
                  position: relative;
                }
              `}</style>
        </div>
        
      )
    }
