import React from 'react'

import '../generic.scss'
import './flexShorthand.scss'


const ColumnContainerStyle = { flexDirection:"column"}
const ColumnContainerExtendedStyle = { margin:0, padding:0, flexDirection:"column", width:"100%", height:"100%", flexGrow:1}
class ColumnContainer extends React.Component {
    render() {
        return ( 
            <div className = "Container"  style = {ColumnContainerStyle.concat(this.props.style)}>
                {this.props.children}
            </div>
        )
    }
}
class ColumnContainerExtended extends React.Component {
    render() {
        return ( 
            <div className = "Container" style = {ColumnContainerExtendedStyle.concat(this.props.style)}>
                {this.props.children}
            </div>
        )
    }
}

class RowContainer extends React.Component {
    render() {
        return (
            <div style={this.props.style} className="Container">
                {this.props.children}
            </div>
        )
    }
}

class RowContainerExtended extends React.Component {
    render() {
        return (
            <div className="Container" style = {this.props.style.concat({ margin:0, padding:0, width:"100%", height:"100%", flexGrow:1})}>
                {this.props.children}
            </div>
        )
    }
}

export {ColumnContainer, ColumnContainerExtended, RowContainer, RowContainerExtended};
