import React from 'react'
import './flexShorthand.scss'

class ColumnContainer extends React.Component {
    render() {
        return ( 
            <div className = "Container"  style = {{display:"flex", flexDirection:"column"}}>
                {this.props.children}
            </div>
        )
    }
}
class ColumnContainerExtended extends React.Component {
    render() {
        return ( 
            <div className = "Container" style = {{display:"flex", flexDirection:"column", height:"100%", flexGrow:"1"}}>
                {this.props.children}
            </div>
        )
    }
}



export {ColumnContainer, ColumnContainerExtended};
